const burger=document.querySelector(".burger"),clos=document.querySelector(".btn-close"),menuItem=document.querySelectorAll(".menu-item");function close(){document.querySelector(".footer__right").classList.remove("active")}burger.addEventListener("click",(function(){document.querySelector(".footer__right").classList.add("active")})),clos.addEventListener("click",close);for(let e=0;e<menuItem.length;e++)menuItem[e].addEventListener("click",()=>{menuItem.forEach(e=>{e.classList.remove("focus")}),menuItem[e].classList.add("focus"),close()});